package com.dkte;

public class MaxNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=6;
		int c=7;
		
		if(a>b) {
			if(a>c)
			{
				System.out.println("a is maximum");
			}
		}
		else if(b>c)
		{
			System.out.println("b is maximum");
		}
		else
		{
			System.out.println("c is maximum");
		}
		

	}

}
